html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%} 
    {% if doc.doctype == 'Sales Invoice' %}
    {% if letter_head and not no_letterhead %} 
    <div class="letter-head">{{ letter_head }}</div> 
    {% endif %} 
    {%- if doc.meta.is_submittable and doc.docstatus==0-%} 
    <!-- <div class="alert alert-info text-center"> 
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>  -->
    {%- endif -%} 
    {%- if doc.meta.is_submittable and doc.docstatus==2-%} 
   <!--  <div class="alert alert-danger text-center"> 
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>  -->
    {%- endif -%} 
    {% if max_pages > 1 %} 
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p> 
    {% endif %} 
    {% endif %} 
{%- endmacro -%} 

{% if doc.doctype == 'Sales Invoice' %}
<style> 
    .table{ 
    border: 2px solid black; 
    color:#000000; 
    font-size:16px; 
    } 
    .tr{ 
    border: 2px solid black; 
    } 

    .print-format table, .print-format tr,  
    .print-format td, .print-format div, .print-format p { 
        font-family: sans-serif; 
        font-size: 15px;
        line-height: 90%; 
        vertical-align: middle; 
    } 
    @media screen { 
        .print-format { 
            width: 210mm; 
            padding: 10mm; 
            min-height: 297mm; 
            footer {page-break-after: always;} 
        } 
    } 
</style> 

<small> 
{{ add_header(0,1,doc,letter_head, no_letterhead) }} 

<table class="table table-condensed"> 
  <tr> 
     {% if doc.company_address %}
     {% set cc = frappe.get_doc("Address", doc.company_address) %}
    <td rowspan="2" align="center"><b><p style="font-size:16px; font:bold;"> 
                      {{ cc.address_title }} <br><br>
                      {{ cc.address_line1 }}<p></b>

                     <p style="font-size:12px;"> {{ cc.address_line2 }}</p>
                      Phone : {{ cc.phone }}, Email : {{ cc.email_id }} <br><br>
                      CIN No.: {{ doc.name }} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GST No.: {{ cc.gstin }} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; State Code: {{ cc.gst_state_number }}
    </td> 
    {% endif %}
  </tr> 
</table> 


<table class = "table table-condensed table-bordered"> 
  <tr> 
     <td style="width: 50%;"> 
        <b>{{ _("Party name.") }}:&nbsp;&nbsp;</b>{{ doc.customer }}</b><br> 
    </td> 
    <td style="width: 50%;"> 
        <b>{{ _("Invoice No.") }}:</b>{{ doc.name }} <br> 
    </td> 
</tr>
 <tr>
    <td style="width: 50%;"> 
        <b>{{ _("Ship To") }}::&nbsp;&nbsp;</b><br> {{ doc.address_display }} 
    </td> 
    <td style="width: 50%;"> 
        <b>{{ _("Date") }}:&nbsp;&nbsp;</b>{{ doc.posting_date }} <br> 
    </td> 
  </tr> 

  <tr>
    <td style="width: 50%;"> 
        <b>{{ _("GST No.") }}:&nbsp;&nbsp;</b> {{ doc.billing_address_gstin }}<br> 
    </td> 
    <td style="width: 50%;"> 
        <b>{{ _("Contact Person") }}:&nbsp;&nbsp;</b> {{ doc.contact_person }} <br> 
    </td> 
  </tr> 

  <tr>
    <td style="width: 50%;"> 
        {% if doc.customer_address %}
        {% set cc = frappe.get_doc("Address", doc.customer_address) %}
        <b>{{ _("State Code") }}:&nbsp;&nbsp;</b> {{ cc.gst_state_number }}<br> 
        {% endif %}
    </td> 
    <td style="width: 50%;"> 
        {% if doc.customer_address %}
        {% set cc = frappe.get_doc("Address", doc.customer_address) %}
        <b>{{ _("Contact No.") }}:&nbsp;&nbsp;</b> {{ cc.phone }}<br> 
        {% endif %}
    </td> 
  </tr> 

  <tr>
    <td style="width: 50%;"> 
        <b>{{ _("PAN1111111111111111111.") }}:</b> <br> 
    </td> 
    <td style="width: 50%;"> 
        <b>{{ _("L R No.") }}:</b> <br> 
    </td> 
  </tr> 
  <tr>
    <td style="width: 50%;"> 
        <b></b> <br> 
    </td> 
    <td style="width: 50%;"> 
        <b>{{ _("Shipped From") }}:</b> <br> {{ doc.place_of_supply }} 
    </td> 
  </tr> 
</table> 

<table class="table table-condensed table-hover table-bordered"> 
        <tr> 
            <th>Sr</th> 
            <th>Description</th> 
            <th class="text-right">Qty</th> 
            <th class="text-right">UOM</th> 
            <th class="text-right">Desc %</th> 
            <th class="text-right">Net Rate</th> 
            <th class="text-right">Amount</th> 
        </tr> 
        {%- for row in doc.items -%} 
        <tr> 
            <td style="width: 3%;">{{ row.idx }}</td> 
            <td style="width: 57%;">{{ row.description }}</td> 
            <td style="width: 10%; text-align: right;">{{ row.qty }}</td> 
            <td style="width: 10%; text-align: right;">{{ row.uom }}</td> 
            <td style="width: 10%; text-align: right;">{{ row.discount_percentage }}</td> 
            <td style="width: 15%; text-align: right;">{{ row.rate }}</td> 
            <td style="width: 15%; text-align: right;">{{ row.amount }}</td> 

        </tr> 
        {%- endfor -%} 
    </tbody> 
</table> 

<table class="table table-condensed table-hover table-bordered"> 
 <tr> 
     <td style="width: 50%;"> 
        {% for t in doc.payment_schedule %}
        {% if t.payment_term %}
        {% set acc = frappe.get_doc("Payment Term", t.payment_term) %}
         <b>{{ _("Pay Terms") }}:&nbsp;&nbsp;</b> {{ acc.payment_term_name }}<br> 
         <b>{{ _("Credit Days") }}:&nbsp;&nbsp;</b> {{ acc.credit_days }}<br> 
         <b>{{ _("Currency") }}:&nbsp;&nbsp;</b> {{ doc.currency }}<br> 
         <b>{{ _("In Words") }}:&nbsp;&nbsp;</b> {{ doc.in_words }}<br> <br>

         <b>{{ _("Terms") }}:&nbsp;&nbsp;</b> {{ doc.terms }}<br> 
        {% endif %}
        {% endfor %}
    </td> 
    <td style="width: 50%;text-align:right;"> 
        <b>{{ _("Sub Total") }}:&nbsp;&nbsp;</b> {{ doc.net_total }}<br> 
    </td> 
</tr>
 <tr>
    <td style="width: 50%;"> 
       <p></p>
    </td> 
    <td style="width: 50%;text-align:right;"> 
        {% for d in doc.taxes %}
        {% if d.account_head %}
        {% set acc = frappe.get_doc("Account", d.account_head) %}
        <b></b> {{ acc.account_name }} {{ d.tax_amount }}</br>
        {% endif %}
        {% endfor %}
    </td> 
  </tr> 
  <tr>
    <td style="width: 50%;"> 
        <b></b> <br> 
    </td> 
    <td style="width: 50%;text-align:right;"> 
        <b>{{ _("Grand Total") }}:</b> {{ doc.grand_total }}<br> 
    </td> 
  </tr> 
   <tr>
    <td style="width: 50%;"> 
        <b>{{ _("Remarks<br> 
                PO NO:4032830825<br> 
                PO DT:19.02.2018<br> 
                TOTAL CRTNS-") }}:</b> <br> 
    </td> 
    <td style="width: 50%;text-align:right;"> 
        <b>{{ _("E & O.E <br>
                For PEGASUS TOY-KRAFT PRIVATE LIMITED<br>
                ( AUTHORISED SIGNATORY )") }}:</b> <br> 
    </td> 
  </tr> 
</table> 


{% else %}

     {%- from "templates/print_formats/standard_macros.html" import add_header,
         render_field -%}

    {% for page in layout %}
    <div class="page-break">
        <div {% if print_settings.repeat_header_footer %} id="header-html" class="hidden-pdf" {% endif %}>
            {{ add_header(loop.index, layout|len, doc, letter_head, no_letterhead, footer, print_settings) }}
        </div>

        {% if print_settings.repeat_header_footer %}
        <div id="footer-html" class="visible-pdf">
            {% if not no_letterhead and footer %}
            <div class="letter-head-footer">
                {{ footer }}
            </div>
            {% endif %}
            <p class="text-center small page-number visible-pdf">
                {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
            </p>
        </div>
        {% endif %}

        {% for section in page %}
        <div class="row section-break">
            {%- if doc._line_breaks and loop.index != 1 -%}<hr>{%- endif -%}
            {%- if doc._show_section_headings and section.label and section.has_data -%}
            <h4 class='col-sm-12'>{{ _(section.label) }}</h4>
            {%- endif -%}
            {% for column in section.columns %}
                <div class="col-xs-{{ (12 / section.columns|len)|int }} column-break">
                {% for df in column.fields %}
                    {{ render_field(df, doc) }}
                {% endfor %}
                </div>
            {% endfor %}
        </div>
        {% endfor %}
    </div>
    {% endfor %}

{% endif %}
